import React, { useState } from 'react';
import './App.css';
import SpeechRecognition, { useSpeechRecognition } from 'react-speech-recognition';
import useClipboard from "react-use-clipboard";

const App=()=>{

const [current,updated]=useState();
  const [isCopied, setCopied] = useClipboard(current);
  const strtListen=()=> SpeechRecognition.startListening({ continuous: true , language :'en-IN' })
  const {transcript,listening,browserSupportsSpeechRecognition} = useSpeechRecognition();

  if (!browserSupportsSpeechRecognition) {
    return <span>Browser doesn't support speech recognition.</span>;
  }

  return(<>
     <div className='container'>
      <h2><></>Speech To Text Contener</h2>
      <br/>
      <p>Converts speech from the microphone to text and makes it
available to your React components.</p>
      <div className='main-content' onClick={()=>updated(transcript)}>
             {transcript}
      </div>


      <div className='btn-style'>
      <button onClick={setCopied}>
       {isCopied ? "Copied üëç" : "Copy To Clipboard üòÑ"}
    </button>
        <button onClick={strtListen}> Start Listening</button>
        <button onClick={SpeechRecognition.stopListening}>Stop Listening</button>
      </div>
     </div>
  </>)
}

export default App;
